package Programs;

public class LargestNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=20;
int b=30;
int c=10;

if(a>b & a>c)
{
	System.out.println(a+ " is Largest");
}
else if(b>c)
{
	System.out.println(b+ " is Largest");	
}
else
{
	System.out.println(c+ " is Largest");
}
	}

}
